mapper.update:
UPDATE menu
   SET name = #{name}
     , intr_dc = #{intrDc}
     , price = #{price}
     , sort = #{sort}
     , menu_group_id = #{menuGroupId}
     , update_at = now()
 WHERE id = #{id}
   AND used = 1

UPDATE menu
   SET file_id = #{fileId}
     , update_at = now()
 WHERE id = #{menuId}
