mapper.insert:
INSERT INTO restaurant(
        name
      , bizr_no
      , tel_no
      , intr_dc
      , min_order_price
      , payment_type
      , owner_id
      , category_id
      , open_time
      , close_time
      , post_cd
      , base_address
      , detail_address
      , sigungu_cd

          , file_id

)
VALUES (
        #{name}
      , #{bizrNo}
      , #{telNo}
      , #{intrDc}
      , #{minOrderPrice}
      , #{paymentType}
      , #{ownerId}
      , #{categoryId}
      , #{openTime}
      , #{closeTime}
      , #{postCd}
      , #{baseAddress}
      , #{detailAddress}
      , #{sigunguCd}

          , #{uploadFile.id}

)

mapper.select:
SELECT id , name , intr_dc FROM restaurant WHERE owner_id = #{ownerId}
AND id > #{lastRestaurantId}
ORDER BY id LIMIT 10

mapper.update:
UPDATE restaurant
   SET name = #{name}
     , bizr_no = #{bizrNo}
     , tel_no = #{telNo}
     , intr_dc = #{intrDc}
     , min_order_price = #{minOrderPrice}
     , payment_type = #{paymentType}
     , open_time = #{openTime}
     , close_time = #{closeTime}
     , category_id = #{categoryId}
     , post_cd = #{postCd}
     , base_address = #{baseAddress}
     , detail_address = #{detailAddress}
     , sigungu_cd = #{sigunguCd}
     , update_at = now()


             , file_id = #{uploadFile.id}


             , file_id = null


 WHERE id = #{id}
