mapper.select:
SELECT id , name , intr_dc FROM restaurant WHERE owner_id = #{ownerId}
AND id > #{lastRestaurantId}
ORDER BY id LIMIT 10

mapper.update:
UPDATE restaurant
   SET name = #{name}
     , bizr_no = #{bizrNo}
     , address = #{address}
     , region_cd = #{regionCd}
     , tel_no = #{telNo}
     , intr_dc = #{intrDc}
     , min_order_price = #{minOrderPrice}
     , payment_type = #{paymentType}
     , open_time = #{openTime}
     , close_time = #{closeTime}
     , category_id = #{categoryId}
     , update_at = now()
 WHERE id = #{id}
